const newsItems = $input.all();

const formattedNews = newsItems.map(item => {

    const contenidoCompleto = item.json.description  || item.json.contentSnippet;
  return {
    title: item.json.title, 
    contenido : contenidoCompleto
  };
});


return formattedNews;

const iaOutput = $input.all();


const usefulNews = iaOutput
  .map(item => {

    const output = JSON.parse(item.json.output);
    return {
      clasificacion: output.clasificacion,
      razon: output.razon,
      text: output.noticia,
      noticia: item.json.noticia 
    };
  })
  .filter(item => item.clasificacion === "Útil");

return usefulNews;

const newsItems = $input.all();

const cleanedNews = newsItems.map(item => {
  const rawText = item.json.contenido;
  const textWithoutHTML = rawText.replace(/<[^>]+>/g, "");
  const cleanText = textWithoutHTML
    .replace(/\n/g, " ") 
    .replace(/\s+/g, " ") 
    .trim(); 

  const rawTitle = item.json.title;
  const titleWithoutHTML = rawTitle.replace(/<[^>]+>/g, "");
  const cleanTitle = titleWithoutHTML
    .replace(/\n/g, " ") 
    .replace(/\s+/g, " ") 
    .trim(); 

  return {
    title: cleanTitle,
    noticia: cleanText
  };
});

return cleanedNews;

const newsItems = $input.all();

const formattedNews = newsItems.map(item => {
  // Extraer el título del output usando una expresión regular
  const match = item.json.output.match(/\*\*(.*?)\*\*/);
  const title = match && match[1] ? match[1].trim() : "Sin título"; // Si no hay título, usa un valor por defecto

  // Escapar el output para evitar problemas con caracteres especiales
  const escapedOutput = JSON.stringify(item.json.output);

  return {
    title: title, // Título extraído
    output: escapedOutput // Output escapado
  };
});

return formattedNews;

const newsItems = $input.all();

const cleanedNews = newsItems.map(item => {
  const rawText = item.json.output;

  // Elimina etiquetas HTML
  const textWithoutHTML = rawText.replace(/<[^>]+>/g, "");

  // Elimina saltos de línea y espacios múltiples
  let cleanText = textWithoutHTML
    .replace(/\n/g, " ") // Reemplaza saltos de línea con espacios
    .replace(/\s+/g, " ") // Reduce múltiples espacios a uno solo
    .replace(/\*/g, " ") // Elimina asteriscos
    .replace(/\#/g, " ") // Elimina almohadillas
    .replace(/\\/g, "") // Elimina barras invertidas
    .replace(/"/g, "'") // Reemplaza comillas dobles con comillas simples
    .trim(); // Elimina espacios al inicio y al final

  // Escapa el texto para JSON
  cleanText = JSON.stringify(cleanText).slice(1, -1);

  // Trunca el texto a 1000 caracteres
  cleanText = cleanText.slice(0, 1000);

  return {
    noticia: cleanText
  };
});

return cleanedNews;

parameters.text:
=Eres un asistente especializado en filtrar noticias relevantes. A continuación, te proporcionaré una noticia. Clasifícala como **útil** o **no útil** basándote en los siguientes criterios:  
- **Temas útiles**: educación, tecnología, innovación, becas, oportunidades académicas, formación, investigación, desarrollo profesional.  
- **Temas no útiles**: protestas, conflictos, política, huelgas, crisis, desastres, controversias.  

Para cada noticia, responde **solo con un JSON** en el siguiente formato:  
{
  "clasificacion": "Útil" o "No útil",
  "noticia": "toda la informacion de la noticia"
}

Noticia: {{ $json.title }}{{ $json.noticia }}

