<style>
        .custom-psicopatologia {
            font-family: Arial, sans-serif;
            margin: 40px;
            line-height: 1.6;
            background-color: #f9f9f9;
            color: #333;
        }
        .custom-psicopatologia h1,
        .custom-psicopatologia h2,
        .custom-psicopatologia h3 {
            color: #2c3e50;
        }
        .custom-psicopatologia h1 {
            text-align: center;
            margin-bottom: 50px;
        }
        .custom-psicopatologia h2 {
            margin-top: 40px;
            border-bottom: 2px solid #e74c3c;
            padding-bottom: 10px;
        }
        .custom-psicopatologia h3 {
            margin-top: 30px;
            color: #e74c3c;
        }
        .custom-psicopatologia p {
            text-align: justify;
        }
        .custom-psicopatologia ul {
            margin-left: 20px;
            list-style-type: disc;
        }
        .custom-psicopatologia li {
            margin-bottom: 10px;
            line-height: 1.5;
        }
        .custom-psicopatologia blockquote {
            margin: 20px 0;
            padding: 20px;
            background-color: #ecf0f1;
            border-left: 10px solid #e74c3c;
        }
        .custom-psicopatologia code {
            background-color: #ecf0f1;
            padding: 2px 4px;
            font-family: "Courier New", Courier, monospace;
        }
        .custom-psicopatologia .example {
            background-color: #eaf2f8;
            padding: 20px;
            border-left: 5px solid #2980b9;
            margin: 20px 0;
        }
        .custom-psicopatologia .challenges {
            background-color: #fdebd0;
            padding: 20px;
            border-left: 5px solid #d35400;
            margin: 20px 0;
        }
        .custom-psicopatologia .benefits {
            background-color: #e8f8f5;
            padding: 20px;
            border-left: 5px solid #16a085;
            margin: 20px 0;
        }
    </style>

